mapper.select:
select goods_id, goods_type_id, name, number, price, gmt_create
from goods
where goods_id = #{goodsId}

select goods_id, classType, brand_id, name, props, business_id, number, unit, price, img_url, gmt_create
from tb_goods
where brand_id = #{brand}
limit #{offset}, #{limit}

select goods_id, classType, name, detail, gmt_create
from tb_goods_detail
where goods_id = #{goodsId}

select goods_id, classType, name, number_info, number_detail, suffix_type, urls_info, urls_detail,  gmt_create  
from tb_goods_image
where goods_id = #{goodsId}

select goods_id, classType, name, evaluate_rank, user_id, content, gmt_create
from tb_goods_evaluate
where goods_id = #{goodsId}
