mapper.select:
select goods_id, category, brand_id, name, props, business_id, number, unit, price, img_url, gmt_create
from tb_goods
where category = #{category}
limit #{offset}, #{limit}

select goods_id, category, brand_id, name, props, business_id, number, unit, price, img_url, gmt_create
from tb_goods
where brand_id = #{brandId}
limit #{offset}, #{limit}

select goods_id, category, brand_id, name, props, business_id, number, unit, price, img_url, gmt_create
from tb_goods
where goods_id = #{goodsId}

select goods_id, category, name, color, model, format, param, props, weight, img_url, market, mall, number
from tb_goods_list
where goods_id = #{goodsId}

select goods_id, category, name, detail, gmt_create
from tb_goods_detail
where goods_id = #{goodsId}

select goods_id, category, name, number_info, number_detail, suffix_type, urls_info, urls_detail,  gmt_create  
from tb_goods_image
where goods_id = #{goodsId}

select goods_id, category, name, evaluate_rank, user_id, content, gmt_create
from tb_goods_evaluate
where goods_id = #{goodsId}

select goods_sku_id, goods_id, category, name, color, model, format, param, props, weight, img_url, market, mall, number, gmt_create
from tb_goods_list
where goods_sku_id = #{goodsSkuId}
